import React, {useEffect, useState} from 'react';
import {Card, CardColumns, CardGroup, Col, Image, Row} from "react-bootstrap";
import Footer from "./Footer";
import axios from "axios";

function Earth(props) {

    const [earth, setEarth] = useState([]);

    useEffect(() =>{
        axios.get("https://api.nasa.gov/EPIC/api/natural/images?api_key=Dvef1dOc51iUgHDH93ncfcFAbriP6uDccUuTIo4x")
            .then(res =>{
                setEarth(res.data)
            })
    }, [])

    return (
        <div className="earth-container">
            <div className="section-1">
                <h1 className="earth-title">Earth</h1>
                <p className="earth-description">Our blue marble</p>
                <div className="scroll">scroll placeholder</div>
            </div>

            <div className="section-2">
                <div className="split">
                    <div className="earth-image-container">
                        {earth.map((el) =>(
                            <Image className="earth-image" src={`${el.image}.jpg`} alt="daily earth image" />
                        ))}
                    </div>
                    <div className="earth-content">
                        <h3 className="earth-content-title">Title</h3>
                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Esse, obcaecati.</p>
                    </div>
                </div>
            </div>

            <Footer />
        </div>
    );
}

export default Earth;
